import React ,{Component} from 'react';
import { Grid, Paper } from '@material-ui/core';
import * as historyService from "../../Axios-Actions/historyService";
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReadMoreReact from 'read-more-react';

class orderHistory extends Component {

    
    constructor() {
        super();
        this.state = {
            task:[],

        };
    }
       componentDidMount(){
           historyService.getAllOrders()
           .then((result) => {
            this.setState({ task: result.data  });
          })
       }

    
    render() {
        const { task } = this.state;
      return (
        <Grid container>

        <Grid item md={1}></Grid>
        <Grid item md={10} xs={12}>
        <ul style={{listStyle:'none',marginLeft:'10px',display:'inline-flex'}}>
      {
                    task.map(t=>(
                        <li key={t._id} style={{marginTop:'20px',marginBottom:'20px'}}>

        <Card style={{maxWidth:'300px',marginLeft:'30px'}} >
      <CardContent>
        <Typography  color="textSecondary" gutterBottom style={{backgroundColor:'#619eff'}}>
        <li> Order:{t._id} </li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Title: {t.title}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Start Time:       {t.starttime}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  End Time:    {t.endtime}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Month: {t.month}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Service Type: {t.servicetype}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Location: {t.location}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li>  Bill:   {(t.month*t.permonth)+(t.pertask)+(t.perhour*(parseInt(t.endtime)-parseInt(t.starttime))*(t.month*30))}</li>
        </Typography>
        <Typography variant="h6" component="h2">
      <li> Service Provider Email: {t.serviceprovideremail}</li>
        </Typography>
 
      </CardContent>
      <CardActions>
      
      </CardActions>
    </Card>


    </li>
                    ))
      }

      </ul>

            

        </Grid>
        
        <Grid item md={2}></Grid>

    </Grid>
      );
    }
  }


 
export default orderHistory ;
