import React ,{Component} from 'react';
import { Grid, Paper } from '@material-ui/core';
import * as historyService from "../../Axios-Actions/historyService";
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';


class orderHistory extends Component {

    
    constructor() {
        super();
        this.state = {
            task:[],

        };
    }
       componentDidMount(){
           historyService.getAllOrders()
           .then((result) => {
            this.setState({ task: result.data });
          })
       }
    render() {
        const { task } = this.state;
      return (
        <Grid container>

        <Grid item md={2}></Grid>
        <Grid item md={8} xs={12}>

        <Card className={classes.root}>
      <CardContent>
        <Typography className={classes.title} color="textSecondary" gutterBottom>
          Word of the Day
        </Typography>
        <Typography variant="h5" component="h2">
        abc
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          adjective
        </Typography>
        <Typography variant="body2" component="p">
          well meaning and kindly.
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card>

            <h3 style={{textAlign:'center'}}>
                Order History
            </h3>
            <ul style={{listStyle:'none'}}>
                {
                    task.map(t=>(
                        <li key={t._id} style={{marginTop:'20px',marginBottom:'20px'}}>
            <Paper elevation={3}>
        <table style={{borderCollapse: "separate", borderSpacing:'30px'}}>
              <thead>
                  <tr >
                  <th >
                      Title
                  </th>
                  <th>
                      Starting Time
                  </th>
                  <th>
                      End time
                  </th>
                  <th>
                      Month
                  </th>
                  <th>
                      Service type
                  </th>
                  <th>
                      Location
                  </th>
                  <th>
                      Total Bill
                  </th>
                  <th>
                      Serviceprovider Email
                  </th>
                  </tr>
              </thead>
              <tbody style={{textAlign:'center'}}>
                 <tr >          
                  <td>
                        {t.title}
                      </td>
                  <td>
                {t.starttime}
                  </td>
                  <td>
             {t.endtime}
                  </td>
                  <td>
             {t.month}
                  </td>
                  <td>
            {t.servicetype}
                  </td>
                  <td>
            {t.location}
                  </td>
                  <td>
            {(t.month*t.permonth)+(t.pertask)+(t.perhour*(parseInt(t.endtime)-parseInt(t.starttime))*(t.month*30))}
                  </td>
                  <td>
            {t.serviceprovideremail}
                  </td>
               
                  </tr>
            
              </tbody>
         
              </table>  
              </Paper>
              </li>
                    ))
                }
            </ul>

        </Grid>
        
        <Grid item md={2}></Grid>

    </Grid>
      );
    }
  }


 
export default orderHistory ;
