import React, { Component } from 'react';
import * as authService from'../../Axios-Actions/authService';
import { Grid, Paper, Typography } from '@material-ui/core';
import img1 from './work.jpg';
import Joi from "joi-browser";


let la,ln;
class customerSignup extends Component {

    render() {
        const { classes } = this.props;
        return(
          <Grid container component="main"  className={classes.root} >
          <CssBaseline />
          <Grid item xs={false} sm={4} md={7} className={classes.image} />
          <Grid item md={5}>
          <div className={classes.paper}> 
            <Avatar className={classes.avatar}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign up
            </Typography>
            <form className={classes.form} >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete="fname"
                    variant="outlined"
                    required
                    fullWidth
                    id="firstName"
                    label="First Name"
                    autoFocus
                    name='fname'
                     onChange={this.onChange}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
                    id="lastName"
                    label="Last Name"
                    autoComplete="lname"
                    name='lname'
                     onChange={this.onChange} 
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                <NativeSelect style={{marginTop:'10px'}}
                    variant="outlined"
                    required
                    fullWidth
                    id="servicetype"
                    label="Enter your Service Type"
                    name="servicetype"
                   
                     onChange={this.onChange}
                   
                  >
                       <option value="">Service Type</option>
                       <option value="maid">maid</option>
                                  <option value="plumber">plumber</option>
                                  <option value="driver">driver</option>
                                  <option value="cook">cook</option>
                                  <option value="tailor">tailor</option>
                                  <option value="electrian">electrician</option>
                  </NativeSelect>
                
                </Grid>
                <Grid item xs={12} sm={6}>
                <TextField
                    variant="outlined"
                    required
                    fullWidth
                    id="phone"
                    label="Enter your Phone Number"
                    name='contact'
                     onChange={this.onChange}
                   
                  />
                  </Grid>
                <Grid item xs={12}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                 
                    autoComplete="email"
                    name='email' 
                     onChange={this.onChange}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
               
                    label="Password"
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    name='password'
                     onChange={this.onChange}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
               
                    label="Confirm Password"
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    name='confirmpassword' 
                     onChange={this.onChange}
                  />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel
                    control={<Checkbox value="allowExtraEmails" color="primary" />}
                    label="I want to receive inspiration, marketing promotions and updates via email."
                  />
                </Grid>
              </Grid>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                className={classes.submit}
                onClick={this.onSubmit}
              >
                Sign Up
              </Button>
              <Grid container justify="flex-end">
                <Grid item>
                  <Link href="#" variant="body2">
                    Already have an account? Sign in
                  </Link>
                </Grid>
              </Grid>
            </form>
          </div>
          <Box mt={5}>
            <Copyright />
          </Box>
        </Grid>
        </Grid>
        )
    }
  }

  export default withStyles(styles, { withTheme: true })(customerSignup);
