import React, { useEffect } from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// @material-ui/icons
import Camera from "@material-ui/icons/Camera";
import Palette from "@material-ui/icons/Palette";
import Favorite from "@material-ui/icons/Favorite";
// core components
import Header from "../../components/Header/Header.js";
import Footer from "../../components/Footer/Footer.js";
import Button from "../../components/CustomButtons/Button.js";
import GridContainer from "../../components/Grid/GridContainer.js";
import GridItem from "../../components/Grid/GridItem.js";
import HeaderLinks from "../../components/Header/HeaderLinks.js";
import NavPills from "../../components/NavPills/NavPills.js";
import Parallax from "../../components/Parallax/Parallax.js";

import profile1 from "../../assets/img/faces/christian.jpg";
import * as customerService from "../../Axios-Actions/customerService"
import studio1 from "../../assets/img/examples/studio-1.jpg";
import studio2 from "../../assets/img/examples/studio-2.jpg";
import studio3 from "../../assets/img/examples/studio-3.jpg";
import studio4 from "../../assets/img/examples/studio-4.jpg";
import studio5 from "../../assets/img/examples/studio-5.jpg";
import work1 from "../../assets/img/examples/olu-eletu.jpg";
import work2 from "../../assets/img/examples/clem-onojeghuo.jpg";
import work3 from "../../assets/img/examples/cynthia-del-rio.jpg";
import work4 from "../../assets/img/examples/mariya-georgieva.jpg";
import work5 from "../../assets/img/examples/clem-onojegaw.jpg";
import * as postService from'../../Axios-Actions/postService';
import styles from "../../assets/jss/material-kit-react/views/profilePage";
import { Card, CardActionArea, CardActions, CardContent, InputAdornment, Paper, TextField, Typography } from "@material-ui/core";
import { PanoramaFishEye } from "@material-ui/icons";
import CustomInput from "../../components/CustomInput/CustomInput.js";
import CustomDropdown from "../../components/CustomDropdown/CustomDropdown.js";
import CustomTabs from "../../components/CustomTabs/CustomTabs.js";

const useStyles = makeStyles(styles);

export default function ProfilePage(props) {




  const [prof,setProfile] = React.useState([]);

  const [sp,setSp] = React.useState([]);
  const [title,setTitle] = React.useState('');
  const [location,setLocation] = React.useState('');
  const [perHour,setPerHour] = React.useState('');
  const [perMonth,setPerMonth] = React.useState('');
  const [description,setDescription] = React.useState('');
  const [perTask,setPerTask] = React.useState('');

  const [updateOpen,setUpdateOpen] = React.useState(false);

  useEffect(() => {
    customerService.getprofile()
    .then((result)=>{setProfile(result.data)});

    customerService.getAllService()
    .then((result)=>{setSp(result.data)});

    postService
    .PostATask(

      title,
      document.getElementById('servicetype').value,
      location,
      perHour,
      perMonth,
      perTask,
      document.getElementById('starttime').value,
      document.getElementById('endtime').value,
      document.getElementById('month').value,
      this.state.description
    )
    .then((result) => {
      // localStorage.setItem("token", result.data);
    console.log("Successfull posted");
      setTimeout(function () {
        window.location = "/";
      }, 2000);
    })
    .catch((err) => {
      this.setState({ invalid: true });
      console.log("Server error");
    });

  },[]);




/*

  newChange=(event)=>{
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    }); 


  };
  editSave=()=>{



    if(this.state.newfname.length>10||this.state.newfname.length<3){
console.log("enter valid firstname");
    }else if(this.state.newlname.length>10||this.state.newlname.length<3){

      console.log("enter valid lastname");

    }else if(this.state.newpass.length<4){


      console.log("enter valid password");
    }else if(this.state.newcontact.length>255||this.state.newcontact.length<11){


      console.log("enter valid contact no");
    }else{




    customerService.editdata(this.state.newfname,this.state.newlname,this.state.newpass,this.state.newcontact)
    .then((result) => {
    console.log("Successfull edited customer data");
      setTimeout(function () {
        window.location = "/customerdashboard";
      }, 2000);
    })
    .catch((err) => {
      this.setState({ invalid: true });
      console.log("Server error");
    });
  }
  }


  handleChange = () => {
    this.setState({updateOpen:!this.state.updateOpen})
  };
*/









  const classes = useStyles();
  const { ...rest } = props;
  const imageClasses = classNames(
    classes.imgRaised,
    classes.imgRoundedCircle,
    classes.imgFluid
  );
  const navImageClasses = classNames(classes.imgRounded, classes.imgGallery);
  return (
    <div>
      <Header
        color="transparent"
        brand="XsSupport"
        rightLinks={<HeaderLinks />}
        fixed
        changeColorOnScroll={{
          height: 200,
          color: "white"
        }}
        {...rest}
      />
      <Parallax small filter image={require("../../assets/img/profile-bg.jpg")} />
      <div className={classNames(classes.main, classes.mainRaised)}>
        <div>
          <div className={classes.container}>
            <GridContainer justify="center">
              <GridItem xs={12} sm={12} md={6}>
                <div className={classes.profile}>
                  <div>
                    <img src={profile1} alt="..." className={imageClasses} />
                  </div>
                  <div className={classes.name}>
      <h3 className={classes.title}>{prof.customername}</h3>
                    <h6>Customer</h6>
                    <Button justIcon link className={classes.margin5}>
                      <i className={"fab fa-twitter"} />
                    </Button>
                    <Button justIcon link className={classes.margin5}>
                      <i className={"fab fa-instagram"} />
                    </Button>
                    <Button justIcon link className={classes.margin5}>
                      <i className={"fab fa-facebook"} />
                    </Button>
                  </div>
                </div>
              </GridItem>
            </GridContainer>
            <div className={classes.description}>
              <p>
                An artist of considerable range, Chet Faker  the name taken by
                Melbourne-raised, Brooklyn-based Nick Murphy  writes, performs
                and records all of his own music, giving it a warm, intimate
                feel with a solid groove structure.{" "}
              </p>
            </div>
            <GridContainer justify="center">
              <GridItem xs={12} sm={12} md={8} className={classes.navWrapper}>
                <NavPills
                  alignCenter
                  color="primary"
                  tabs={[
                    {
                      tabButton: "Post A Task",
                      tabIcon: Camera,
                      tabContent: (
                        <GridContainer justify="center">
                          <GridItem xs={12} sm={12} md={10}>

                          <NavPills
                  alignCenter
                  color="primary"
                  tabs={[
                    {
                      tabButton: "Post A Task",
                      tabIcon: Camera,
                      tabContent: (
                        <GridContainer justify="center">
                          <GridItem xs={12} sm={12} md={11}>
                          <Paper elevation={3}>
                            <GridContainer justify="center">


                           <GridItem xs={10} md={10} sm={6}>
                           <CustomInput style={{justify:'center'}}
                      labelText="TITLE..."
                      value={title} onChange={this.handleChange}
                      id="title"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>

                    <GridItem xs={10} md={5} sm={6}>
                           <CustomInput
                           onChange={this.handleChange}
                           name= 'servicetype'
                      labelText="Service Type..."
                      id="servicetype"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>
                     <GridItem xs={10} md={5} sm={6}>
                           <CustomInput
                           onChange={this.handleChange}
                      labelText="Location..."
                      id="location"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{

                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>
                     <GridItem xs={10} md={5} sm={6}>
                           <CustomInput
                            value={perTask}  onChange={this.handleChange}
                      labelText="Bidding per Task..."
                      id="email"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>
                     <GridItem xs={10} md={5} sm={6}>
                           <CustomInput
                            value={perHour}  onChange={this.handleChange}
                      labelText="Bidding per Hour..."
                      id="email"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>
                     <GridItem xs={10} md={7} sm={6}>
                           <CustomInput
                            value={perMonth}  onChange={this.handleChange}
                      labelText="Bidding per Month..."
                      id="email"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>
                    <GridItem xs={6} md={6}>
                    <TextField
                        onChange={this.handleChange} 
                        name="starttime"
    id="starttime"
    label="Start time"
    type="time"
    defaultValue="07:30"
    className={classes.textField}
    InputLabelProps={{
      shrink: true,
    }}
    inputProps={{
      step: 300, // 5 min
    }}
  />
                    </GridItem>
                    <GridItem xs={6} md={6}>
                    <TextField
                    onChange={this.handleChange} 
                    name="endtime"
    id="endtime"
    label="End Time"
    type="time"
    defaultValue="07:30"
    className={classes.textField}
    InputLabelProps={{
      shrink: true,
    }}
    inputProps={{
      step: 300, // 5 min
    }}
  />
                    </GridItem>
                     <GridItem xs={10} md={11} sm={6}>
                           <CustomInput
                           value={description}name="description"onChange={this.handleChange}
                      labelText="Description..."
                      id="email"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        type: "email",
                        endAdornment: (

                          <InputAdornment position="end">

                          </InputAdornment>

                        )
                      }}
                    />  </GridItem>



                    <GridItem xs={3} md={4} >

                    </GridItem>
                    <GridItem xs={6} md={4} >
                    <Button>POST</Button>
</GridItem>
<GridItem xs={3} md={4} >

</GridItem>

                           </GridContainer>
                           </Paper>
                          </GridItem>

                        </GridContainer>
                      )
                    }] }/>
                          </GridItem>

                        </GridContainer>
                      )
                    },
                    {
                      tabButton: "Work",
                      tabIcon: Palette,
                      tabContent: (
                        <GridContainer justify="center">
                          <GridItem xs={12} sm={12} md={4}>
                            <img
                              alt="..."
                              src={work1}
                              className={navImageClasses}
                            />
                            <img
                              alt="..."
                              src={work2}
                              className={navImageClasses}
                            />
                            <img
                              alt="..."
                              src={work3}
                              className={navImageClasses}
                            />
                          </GridItem>
                          <GridItem xs={12} sm={12} md={4}>
                            <img
                              alt="..."
                              src={work4}
                              className={navImageClasses}
                            />
                            <img
                              alt="..."
                              src={work5}
                              className={navImageClasses}
                            />
                          </GridItem>
                        </GridContainer>
                      )
                    },
                    {
                      tabButton: "Service Providers",
                      tabIcon: PanoramaFishEye,
                      tabContent: (
                        <GridContainer justify="center">
                          <GridItem xs={12} sm={12} md={12}>
                        <GridContainer justify="center">
                           <GridItem md={6}>
                           <div>
                          <Typography variant="h6">Search</Typography>
                          <TextField
                          label="Search..."/>
                        </div>
                           </GridItem>


                           <GridItem md={6}>
                           <div>
                          <Typography variant="h6">Category</Typography>
                          <CustomDropdown/>
                        </div>
                           </GridItem>
                           </GridContainer>
                          <ul style={{ listStyle: 'none', display: 'inline-flex', flexWrap: 'wrap' }}>
                            {
                                sp.map(s => (
                                    <li key={s.id}>
                                        <Card style={{ maxWidth: '200px', marginLeft: '20px', marginTop: '10px' }} >
                                            <CardActionArea>
                                                <img src={studio1} style={{ width: '200px', maxHeight: '150px' }} />
                                                <CardContent>
                                                    <Typography gutterBottom variant="h6" component="h2">
                                                        {s.firstname} {s.lastname}
                                                    </Typography>
                                                    <Typography gutterBottom variant="subtitle1" component="h2">
                                                        {s.servicetype}
                                                    </Typography>
                                                </CardContent>
                                            </CardActionArea>
                                            <CardActions>

                                                <Button size="small" color="primary" style={{ marginLeft: '20px' }}
                                                    onClick={() => this.setState({ openService: !this.state.openService }, this.getData(s._id))}>
                                                    View Details
        </Button>

                                            </CardActions>
                                        </Card>
                                    </li>

                                ))
                            }
                        </ul>




                          </GridItem>

                        </GridContainer>
                      )
                    }
                  ]}
                />
              </GridItem>
            </GridContainer>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
