import React ,{Component} from 'react';
import { Grid, Paper } from '@material-ui/core';
import * as historyService from "../../Axios-Actions/historyService";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';


  

  function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }
  
  const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
  ];

  
const StyledTableCell = theme => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
})(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);


class OrderHistory extends Component {
    constructor() {
        super();
        this.state = {
            task:[],

        };
    }
       componentDidMount(){
           historyService.getAllOrders()
           .then((result) => {
            this.setState({ task: result.data });
          })
       }
    render() {
        const { task } = this.state;
      return (
        <Grid container>
      
        <Grid item md={2}></Grid>
        <Grid item md={8} xs={12}>



        <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Dessert (100g serving)</StyledTableCell>
            <StyledTableCell align="right">Calories</StyledTableCell>
            <StyledTableCell align="right">Fat&nbsp;(g)</StyledTableCell>
            <StyledTableCell align="right">Carbs&nbsp;(g)</StyledTableCell>
            <StyledTableCell align="right">Protein&nbsp;(g)</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <StyledTableRow key={row.name}>
              <StyledTableCell component="th" scope="row">
                {row.name}
              </StyledTableCell>
              <StyledTableCell align="right">{row.calories}</StyledTableCell>
              <StyledTableCell align="right">{row.fat}</StyledTableCell>
              <StyledTableCell align="right">{row.carbs}</StyledTableCell>
              <StyledTableCell align="right">{row.protein}</StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>








            <h3 style={{textAlign:'center'}}>
                Order History
            </h3>
            <ul style={{listStyle:'none'}}>
                {
                    task.map(t=>(
                        <li key={t._id} style={{marginTop:'20px',marginBottom:'20px'}}>
            <Paper elevation={3}>
        <table style={{borderCollapse: "separate", borderSpacing:'30px'}}>
              <thead>
                  <tr >
                  <th >
                      Title
                  </th>
                  <th>
                      Starting Time
                  </th>
                  <th>
                      End time
                  </th>
                  <th>
                      Month
                  </th>
                  <th>
                      Service type
                  </th>
                  <th>
                      Location
                  </th>
                  <th>
                      Total Bill
                  </th>
                  <th>
                      Serviceprovider Email
                  </th>
                  </tr>
              </thead>
              <tbody style={{textAlign:'center'}}>
                 <tr >          
                  <td>
                        {t.title}
                      </td>
                  <td>
                {t.starttime}
                  </td>
                  <td>
             {t.endtime}
                  </td>
                  <td>
             {t.month}
                  </td>
                  <td>
            {t.servicetype}
                  </td>
                  <td>
            {t.location}
                  </td>
                  <td>
            {(t.month*t.permonth)+(t.pertask)+(t.perhour*(parseInt(t.endtime)-parseInt(t.starttime))*(t.month*30))}
                  </td>
                  <td>
            {t.serviceprovideremail}
                  </td>
               
                  </tr>
            
              </tbody>
         
              </table>  
              </Paper>
              </li>
                    ))
                }
            </ul>

        </Grid>
        
        <Grid item md={2}></Grid>

    </Grid>
      );
    }
  }


 
  export default withStyles(styles)(OrderHistory);
